import React from 'react';

// Importamos dinámicamente las imágenes
const importarImagen = (nombreImagen) => {
    try {
        return require(`../imagenes/portfolio/${nombreImagen}`).default;
    } catch (err) {
        console.error(`No se pudo cargar la imagen ${nombreImagen}:`, err);
        return 'placeholder.jpg'; // Imagen de marcador de posición
    }
};

const TarjetaPortafolio = ({ titulo, descripcion, enlace, referencia, imagen, id }) => {
    const imagenSrc = importarImagen(imagen);

    return (
        <div className="card cardPorta" style={{ minWidth: '18rem' }}>
            <div className="card-body">
                <h5 className="card-title">{titulo || 'Sin título'}</h5>
                {descripcion && <p>{descripcion}</p>}
                {enlace && (
                    <a href={enlace} target="_blank" rel="noopener noreferrer" className="card-link">
                        <img id={id} className="imagen" src={imagenSrc} alt={titulo} />
                    </a>
                )}
                {referencia && (
                    <button className="btn btn-primary">
                        <a className="referenciaPortafolios" href={referencia} target="_blank" rel="noopener noreferrer">Referencia</a>
                    </button>
                )}
            </div>
        </div>
    );
};

export default TarjetaPortafolio;
