import React, { useState } from 'react';
import Acordeon from './Acordeon';
import TarjetaPortafolio from './TarjetasPortafolios';
import TarjetasTitulos from './TarjetasTitulos';
import TarjetasReferencias from './TarjetasReferencias';
import '../estilos/App.css';

const AnimacionSeccionConBordes = ({ title, data, isDirectContent, isPortfolio = false, isTarjetasTitulos = false, isTarjetasReferencias = false }) => { 
    const [isOpen, setIsOpen] = useState(false);

    const toggleSection = () => {
        setIsOpen(!isOpen);
        setTimeout(() => {
            const content = document.querySelector('.content');
            const footerHeight = document.querySelector('footer').offsetHeight;
            const contentHeight = content.scrollHeight;
            const windowHeight = window.innerHeight;
            const newHeight = contentHeight + footerHeight > windowHeight ? contentHeight : windowHeight - footerHeight;
            content.style.minHeight = `${newHeight}px`;
        }, 300); // Ajustar después de la animación de despliegue
    };

    return (
        <div className={`seccionConBordes ${isOpen ? 'open' : ''}`}>
            <div className="header acordeon-toggle tarjetas-toggle" onClick={toggleSection}>
                <h4>{title}</h4>
                <i className={`fa-solid fa-chevron-down ${isOpen ? 'rotate' : ''}`}></i>
            </div>
            {isOpen && (
                <div className="content">
                    {isDirectContent ? (
                        data.map((item, index) => <p key={index}>{item}</p>)
                    ) : isPortfolio ? (
                        <div className="contTarjetas">
                            {data.map((proyecto, index) => (
                                <TarjetaPortafolio
                                    key={index}
                                    titulo={proyecto.titulo}
                                    descripcion={proyecto.descripcion}
                                    enlace={proyecto.enlace}
                                    referencia={proyecto.referencia}
                                    imagen={proyecto.imagen}
                                    id={proyecto.id}
                                />
                            ))}
                        </div>
                    ) : isTarjetasTitulos ? (
                        <TarjetasTitulos data={data} />
                    ) : isTarjetasReferencias ? (
                        <TarjetasReferencias data={data} />
                    ) : (
                        <ul className="acordeones">
                            {data.map((item, index) => (
                                <Acordeon key={index} lugar={item.lugar} descripcion={item.descripcion} />
                            ))}
                        </ul>
                    )}
                </div>
            )}
        </div>
    );
};

export default AnimacionSeccionConBordes;
