import React, { useState } from 'react';
import '../../App.css';

const AnimacionSeccionConBordes = ({ title, children }) => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleSection = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className={`seccionConBordes ${isOpen ? 'open' : ''}`}>
            <div className="otraClase" onClick={toggleSection}>
                <h4 className="header-title">{title}</h4>
                <i className={`fa-solid fa-chevron-down ${isOpen ? 'rotate' : ''}`}></i>
            </div>
            {isOpen && <div className="contenido">{children}</div>}
        </div>
    );
};

export default AnimacionSeccionConBordes;
