import React, {useState, useEffect} from "react";
// import '../../src/estilos/componentes/paginas/inicio.css'
import '../../src/App.css'


const Inicio = (props) => {
    const [cartaDePresentacion, setCartaDePresentacion] = useState({carta: ""});
    useEffect(() => {
            const obtenerDatos = async () => {
                try {
                    const response = await fetch("../../data/datos.json");
                    if (!response.ok) {
                        throw new Error("sin datos");
                    }
                    const data = await response.json();
                    setCartaDePresentacion(data.cartaDePresentacion[0]);
                } catch (error) {
                    console.error("Error al obtener datos", error);
                }
            };
            obtenerDatos()
    }, []);
    return (
        <section className="Inicio">
        <h3>Inicio</h3>
        <div className="seccionConBordes">
            <h4>Carta de presentacion</h4>
            <div className="Carta" id="unaCarta">{cartaDePresentacion.carta}</div>
        </div>
        <div className="seccionConBordes">
            <h4>Portafolios web</h4>  
            <div className="Portafolios contTarjetas" id="unPortafolios">
            </div>
        </div>
    </section>
    );
}

export default Inicio;
