import React, { useState } from 'react';
import '../../App.css';

const AnimacionSeccionConBordes = ({ title }) => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleHeader = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className={`header ${isOpen ? 'scale' : ''}`} onClick={toggleHeader}>
            <h4 className="header-title">{title}</h4>
            <i className={`fa-solid fa-chevron-down ${isOpen ? 'rotate' : ''}`}></i>
        </div>
    );
};

export default AnimacionSeccionConBordes;
