import React, { useState, useEffect } from "react";
import '../App.css'
import {obtenerDatosExperiencia, obtenerDatosReferencias} from "../api.js";
import AnimacionSeccionConBordes from "../componentes/AnimacionSeccionConBordes.js";

const Experiencia = () => {
    const [datosExperiencia, setDatosExperiencia] = useState([]);
    const [datosReferencias, setDatosReferencias] = useState([]);

    useEffect(() => {
        const obtenerDatos = async () => {
            const experiencia = await obtenerDatosExperiencia();
            setDatosExperiencia(experiencia);

            const referencias = await obtenerDatosReferencias();
            setDatosReferencias(referencias);
        };
        obtenerDatos();
    }, []);

    return (
        <section className="Experiencia">
            <h3>Experiencia laboral</h3>
            <AnimacionSeccionConBordes title="Empleador" data={datosExperiencia} />
            <AnimacionSeccionConBordes 
                title="Referencias"
                data={datosReferencias}
                isTarjetasReferencias={true} // Establecemos isTarjetasReferencias en true
            />
        </section>
    );
}

export default Experiencia;
