import React, { useState, useEffect } from "react";
import '../estilos/App.css'
import { obtenerDatosExperiencia, obtenerDatosReferencias } from "../api.js";
import Acordeon from "../componentes/Acordeon";
import TarjetasReferencias from "../componentes/TarjetasReferencias";

const Experiencia = () => {
    const [datosExperiencia, setDatosExperiencia] = useState([]);
    const [datosReferencias, setDatosReferencias] = useState([]);
    const [isExperienciaOpen, setIsExperienciaOpen] = useState(false);
    const [isReferenciasOpen, setIsReferenciasOpen] = useState(false);

    useEffect(() => {
        const obtenerDatos = async () => {
            const experiencia = await obtenerDatosExperiencia();
            setDatosExperiencia(experiencia);

            const referencias = await obtenerDatosReferencias();
            setDatosReferencias(referencias);
        };
        obtenerDatos();
    }, []);

    return (
        <section className="Experiencia">
            <h3>Experiencia laboral</h3>
            <div className={`seccionConBordes ${isExperienciaOpen ? 'open' : ''}`}>
                <div className="header" onClick={() => setIsExperienciaOpen(!isExperienciaOpen)}>
                    <h4>Empleador</h4>
                    <i className={`fa-solid fa-chevron-down ${isExperienciaOpen ? 'rotate' : ''}`}></i>
                </div>
                {isExperienciaOpen && (
                    <div className="content">
                        <ul className="acordeones">
                            {datosExperiencia.map((item, index) => (
                                <Acordeon key={index} lugar={item.lugar} descripcion={item.descripcion} />
                            ))}
                        </ul>
                    </div>
                )}
            </div>
            <div className={`seccionConBordes ${isReferenciasOpen ? 'open' : ''}`}>
                <div className="header" onClick={() => setIsReferenciasOpen(!isReferenciasOpen)}>
                    <h4>Referencias</h4>
                    <i className={`fa-solid fa-chevron-down ${isReferenciasOpen ? 'rotate' : ''}`}></i>
                </div>
                {isReferenciasOpen && (
                    <div className="content">
                        <TarjetasReferencias data={datosReferencias} />
                    </div>
                )}
            </div>
        </section>
    );
}

export default Experiencia;
