export const obtenerCartaDePresentacion = async () => {
    try {
        const response = await fetch('/data/datos.json');
        const datos = await response.json();
        return datos.cartaDePresentacion[0].carta;
    } catch (error) {
        console.error("Error al obtener datos", error);
        return null;
    }
};

export const obtenerDatosPortafolios = async () => {
    try {
        const response = await fetch('/data/datos.json'); 
        if (!response.ok) {
            throw new Error('No se pudieron obtener los datos');
        }
        const data = await response.json();
        return data.datosPortafolios;
    } catch (error) {
        console.error('Error al obtener datos', error);
        return [];
    }
};
