import React, { useState, useEffect } from "react";
import '../../src/App.css'
import {obtenerDatosExperiencia, obtenerDatosReferencias} from "../api.js";
import { cargarAcordeon, animarAcordeon } from "../funciones.js";
import { losAcordeones } from "../funciones.js";

const Experiencia = (props) => {
    const [datosExperiencia, setDatosExperiencia] = useState([]);
    const [datosReferencias, setDatosReferencias] = useState([]);

    useEffect(() => {
        const obtenerDatos = async () => {
            const experiencia = await obtenerDatosExperiencia();
            setDatosExperiencia(experiencia);

            const referencias = await obtenerDatosReferencias();
            setDatosReferencias(referencias);

            const acordeonExperiencia = document.getElementById('acordeonExperiencia');
            cargarAcordeon(acordeonExperiencia, experiencia);
            animarAcordeon(acordeonExperiencia);

        };
        obtenerDatos();
    }, []);
    return (
        <section className="Experiancia">
            <h3>Experiencia laboral</h3>
            <div  className="seccionConBordes">
                <h4>Empleador</h4>
                <ul id="acordeonExperiencia" className="acordeones">
                    {datosExperiencia.map((dato, index) => (
                        <div key={index} dangerouslySetInnerHTML={{ __html: losAcordeones(dato) }} />
                    ))}
                </ul>
            </div>
            <div  className="seccionConBordes">
                <h4>Referencias</h4>
                <div className="contTarjetas" id="tarjetasReferencias">
                {datosReferencias.map((dato, index) => (
                        <div key={index} className="card" style={{ minWidth: '18rem' }}>
                            <div className="card-body">
                                <h5 className="card-title">{dato.nombre}</h5>
                                {dato.telefono && (
                                    <p><i className="fa-solid fa-phone iconoCard"></i>{dato.telefono}</p>
                                )}
                                {dato.correo && (
                                    <p><i className="fa-solid fa-envelope iconoCard"></i>{dato.correo}</p>
                                )}
                                {dato.linkedin && (
                                    <a href={dato.linkedin} target="_blank" rel="noopener noreferrer" className="enlaceLinked">
                                        <i className="fa-brands fa-linkedin linkedRefes"></i>
                                    </a>
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default Experiencia;
