import './App.css';
import React, { useEffect } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { ajustarAltura, addAjustarAltura, removeAjustarAltura } from './funcionesAuxiliares';

// paginas
import Inicio from './paginas/inicio';
import Formacion from './paginas/formacion';
import Experiencia from './paginas/experiencia';

// layout
import Header from './componentes/layout/header';
import Nav from './componentes/layout/nav';
import Footer from './componentes/layout/footer';

function App() {

  useEffect(() => {
    addAjustarAltura();

    return () => {
      removeAjustarAltura();
    };
  }, []);

  return (
    <div className='App'>
      <Header nombre='Pablo Iván Bárcena' imagen='cv'/>
      <BrowserRouter>
        <Nav/>
        <main className="holder content">
          <Routes>      
            <Route path='inicio' element={<Inicio/>}/>
            <Route path='formacion' element={<Formacion/>}/>
            <Route path='experiencia' element={<Experiencia/>}/>
          </Routes>                      
        </main>
      </BrowserRouter>  
      <a href="https://wa.me/541154550660?text=Hola Ivan" className="btn-wsp" target="_blank">
        <i className="fa-brands fa-whatsapp"></i>
      </a>
      <Footer/>
    </div>
  );
}

export default App;
