import React, { useState, useEffect } from "react";
import '../estilos/App.css';
import { obtenerCartaDePresentacion, obtenerDatosPortafolios } from "../api.js";
import TarjetaPortafolio from "../componentes/TarjetasPortafolios";

const Inicio = () => {
    const [cartaDePresentacion, setCartaDePresentacion] = useState([]);
    const [portafolios, setPortafolios] = useState([]);
    const [isCartaOpen, setIsCartaOpen] = useState(false);
    const [isPortafoliosOpen, setIsPortafoliosOpen] = useState(false);

    useEffect(() => {
        const obtenerDatos = async () => {
            const carta = await obtenerCartaDePresentacion();
            setCartaDePresentacion(carta);

            const datosPortafolios = await obtenerDatosPortafolios();
            setPortafolios(datosPortafolios);
        };
        obtenerDatos();
    }, []);

    return (
        <section className="Inicio">
            <h3>Inicio</h3>
            <div className={`seccionConBordes ${isCartaOpen ? 'open' : ''}`}>
                <div className="header" onClick={() => setIsCartaOpen(!isCartaOpen)}>
                    <h4>Carta de presentación</h4>
                    <i className={`fa-solid fa-chevron-down ${isCartaOpen ? 'rotate' : ''}`}></i>
                </div>
                {isCartaOpen && (
                    <div className="content">
                        {cartaDePresentacion.map((item, index) => <p key={index}>{item.carta}</p>)}
                    </div>
                )}
            </div>
            <div className={`seccionConBordes ${isPortafoliosOpen ? 'open' : ''}`}>
                <div className="header" onClick={() => setIsPortafoliosOpen(!isPortafoliosOpen)}>
                    <h4>Portafolios web</h4>
                    <i className={`fa-solid fa-chevron-down ${isPortafoliosOpen ? 'rotate' : ''}`}></i>
                </div>
                {isPortafoliosOpen && (
                    <div className="content contTarjetas">
                        {portafolios.map((proyecto, index) => (
                            <TarjetaPortafolio
                                key={index}
                                titulo={proyecto.titulo}
                                descripcion={proyecto.descripcion}
                                enlace={proyecto.enlace}
                                referencia={proyecto.referencia}
                                imagen={proyecto.imagen}
                                id={proyecto.id}
                            />
                        ))}
                    </div>
                )}
            </div>
        </section>
    );
}

export default Inicio;
