import React, { useState, useEffect } from "react";
import '../../src/App.css';
import { 
    obtenerDatosSecundariaYSuperior, 
    obtenerDatosTecnicaturasYCursos, 
    obtenerDatosCertificaciones 
} from "../api.js";
import Acordeon from "../componentes/layout/acordeon.js";

const Formacion = (props) => {
    const [datosSecundariaYSuperior, setDatosSecundariaYSuperior] = useState([]);
    const [datosTecnicaturasYCursos, setDatosTecnicaturasYCursos] = useState([]);
    const [datosCertificaciones, setDatosCertificaciones] = useState([]);

    useEffect(() => {
        const obtenerDatos = async () => {
            const secundariaYSuperior = await obtenerDatosSecundariaYSuperior();
            setDatosSecundariaYSuperior(secundariaYSuperior);

            const tecnicaturasYCursos = await obtenerDatosTecnicaturasYCursos();
            setDatosTecnicaturasYCursos(tecnicaturasYCursos);

            const certificaciones = await obtenerDatosCertificaciones();
            setDatosCertificaciones(certificaciones);
        };
        obtenerDatos();
    }, []);
    return (
        <section className="Formacion">
                <h3>Formacion</h3>
                <div  className="seccionConBordes">
                    <h4>Educacion secundaria y superior</h4>
                    <ul id="acordeonFormacion" className="acordeones">
                        {datosSecundariaYSuperior.map((dato, index) => (
                        <Acordeon key={index} lugar={dato.lugar} descripcion={dato.descripcion} />
                        ))}
                    </ul>
                </div>
                <div  className="seccionConBordes">
                    <h4>Tecnicaturas y cursos</h4>
                    <ul id="acordeonCursos" className="acordeones">
                        {datosTecnicaturasYCursos.map((dato, index) => (
                        <Acordeon key={index} lugar={dato.lugar} descripcion={dato.descripcion} />
                        ))}
                    </ul>
                </div>
                <div className="seccionConBordes">
                    <h4>Titulos y certificaciones</h4>
                    <div className="contTarjetas" id="tarjetasCertificaciones">
                        {datosCertificaciones.map((dato, index) => (
                            <div key={index} className="card" id="tarjetaCertis" style={{ minWidth: '18rem' }}>
                                <div className="card-body">
                                    <h5 className="card-title">{dato.nombre}</h5>
                                    <button className="card-link botonCertis">
                                        <img id={dato.id} className="imagen" src={dato.imagen} alt={dato.nombre} />
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>  
                </div>          
            </section>
    );
}

export default Formacion;
