import React, { useState } from 'react';
import Acordeon from './Acordeon';
import TarjetaPortafolio from '../TarjetasPortafolios';
import TarjetasTitulos from '../TarjetasTitulos'; // Importamos TarjetasTitulos
import '../../App.css';

const AnimacionSeccionConBordes = ({ title, data, isDirectContent, isPortfolio = false, isTarjetasTitulos = false }) => { // Agregamos isTarjetasTitulos
    const [isOpen, setIsOpen] = useState(false);

    const toggleSection = () => {
        setIsOpen(!isOpen);
    };

    return (
        <div className={`seccionConBordes ${isOpen ? 'open' : ''}`}>
            <div className="header" onClick={toggleSection}>
                <h4>{title}</h4>
                <i className={`fa-solid fa-chevron-down ${isOpen ? 'rotate' : ''}`}></i>
            </div>
            {isOpen && (
                <div className="content">
                    {isDirectContent ? (
                        data.map((item, index) => <p key={index}>{item}</p>)
                    ) : isPortfolio ? (
                        <div className="contTarjetas">
                            {data.map((proyecto, index) => (
                                <TarjetaPortafolio
                                    key={index}
                                    titulo={proyecto.titulo}
                                    descripcion={proyecto.descripcion}
                                    enlace={proyecto.enlace}
                                    referencia={proyecto.referencia}
                                    imagen={proyecto.imagen}
                                    id={proyecto.id}
                                />
                            ))}
                        </div>
                    ) : isTarjetasTitulos ? ( // Verificamos si isTarjetasTitulos es true
                        <TarjetasTitulos data={data} /> // Renderizamos TarjetasTitulos con los datos recibidos
                    ) : (
                        <ul className="acordeones">
                            {data.map((item, index) => (
                                <Acordeon key={index} lugar={item.lugar} descripcion={item.descripcion} />
                            ))}
                        </ul>
                    )}
                </div>
            )}
        </div>
    );
};

export default AnimacionSeccionConBordes;
