import React, { useState, useEffect } from "react";
import '../../src/App.css';
import { obtenerCartaDePresentacion, obtenerDatosPortafolios } from "../api.js";
import AnimacionSeccionConBordes from "../componentes/layout/AnimacionSeccionConBordes.js";

const Inicio = () => {
    const [cartaDePresentacion, setCartaDePresentacion] = useState([]);
    const [portafolios, setPortafolios] = useState([]);

    useEffect(() => {
        const obtenerDatos = async () => {
            const carta = await obtenerCartaDePresentacion();
            setCartaDePresentacion(carta);

            const datosPortafolios = await obtenerDatosPortafolios();
            setPortafolios(datosPortafolios);
        };
        obtenerDatos();
    }, []);

    return (
        <section className="Inicio">
            <h3>Inicio</h3>
            <AnimacionSeccionConBordes 
                title="Carta de presentación" 
                data={cartaDePresentacion.map(item => ({ lugar: '', descripcion: item.carta }))}
            />
            <div className="seccionConBordes">
                <h4>Portafolios web</h4>  
                <div className="contTarjetas" id="unPortafolios">
                    {portafolios.map((proyecto, index) => (
                        <div key={index} className="card cardPorta" style={{ minWidth: '18rem' }}>
                            <div className="card-body">
                                <h5 className="card-title">{proyecto.titulo || 'Sin título'}</h5>
                                {proyecto.descripcion && <p>{proyecto.descripcion}</p>}
                                {proyecto.enlace && (
                                    <a href={proyecto.enlace} target="_blank" rel="noopener noreferrer" className="card-link">
                                        <img id={proyecto.id} className="imagen" src={proyecto.imagen || 'placeholder.jpg'} alt={proyecto.titulo} />
                                    </a>
                                )}
                                {proyecto.referencia && (
                                    <button className="btn btn-primary">
                                        <a className="referenciaPortafolios" href={proyecto.referencia} target="_blank" rel="noopener noreferrer">Referencia</a>
                                    </button>
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default Inicio;
