import React, {useState, useEffect} from "react";
// import '../../src/estilos/componentes/paginas/inicio.css'
import '../../src/App.css'
import { obtenerCartaDePresentacion, obtenerDatosPortafolios } from "../api.js";


const Inicio = (props) => {
    
    const [cartaDePresentacion, setCartaDePresentacion] = useState('');
    const [portafolios, setPortafolios] = useState([]);

    useEffect(() => {
        const obtenerDatos = async () => {
            const carta = await obtenerCartaDePresentacion();
            if (carta !== null) {
                setCartaDePresentacion(carta);
            }

            const datosPortafolios = await obtenerDatosPortafolios();
            setPortafolios(datosPortafolios);
        };
        obtenerDatos();
    }, []);
    
    return (
        <section className="Inicio">
            <h3>Inicio</h3>
            <div className="seccionConBordes">
                <h4>Carta de presentaci√≥n</h4>
                <div className="Carta" id="unaCarta">
                    {cartaDePresentacion && <p>{cartaDePresentacion}</p>}
                </div>
            </div>
            <div className="seccionConBordes">
                <h4>Portafolios web</h4>  
                <div className="Portafolios contTarjetas" id="unPortafolios">
                    {portafolios.map((proyecto, index) => (
                        <div key={index} className="tarjetaPortafolio">
                            <h5>{proyecto.titulo}</h5>
                            <p>{proyecto.descripcion}</p>
                            <a href={proyecto.enlace} target="_blank" rel="noopener noreferrer">Ver proyecto</a>
                            <img src={proyecto.imagen} alt={proyecto.titulo} />
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

export default Inicio;
