export const losAcordeones = (dato) => {
    return `<li class="list">
                <div class="otraClase">
                    <h4 class="lugar">${dato.lugar}</h4><i class="fa-solid fa-chevron-down"></i>
                </div>
                <p class="descripcion">${dato.descripcion}</p>
            </li>`;
};

// animacion de acordeones
export const cargarAcordeon = (acordeon, array) => {
    if (array.length > 0) {
        array.forEach(elemento => {
            acordeon.innerHTML += losAcordeones(elemento);
        });
    }
};

export const animarAcordeon = (acordeon) => {
    acordeon.addEventListener('click', (e) => {
        const targetElement = e.target.closest('.list');
        if (targetElement) {
            targetElement.classList.toggle('scale');
            targetElement.querySelector('.otraClase').children[1].classList.toggle('rotate');
        }
    });
};
