import React from 'react';
import '../../estilos/componentes/layout/header.css'
import datos from '../../public/data/datos.json'


const Header = (props) => {

    const [datosHeader, setDatosHeader] = useState({ nombre: '', imagen: '' });

    useEffect(() => {
        // Suponiendo que los datos de header están en la propiedad "headerData"
        setDatosHeader(datos.datosHeader);
    }, []);

    return (
        <div>
            <header className="encabezado">
                <div className="holder">
                    <div className="elh1">
                        <h1>{datosHeader.nombre}</h1>
                        <p>Curriculum Vitae</p>
                    </div>
                </div>
            </header>
            <div><img 
                        src={`${process.env.PUBLIC_URL}/imagenes/${datosHeader.imagen}.jpg`} 
                        alt="foto" className="foto"
                />
            </div>
        </div>
    );
}

export default Header;
